// Home.js
import React, { useState } from 'react';
import axios from 'axios';
import './Home.css'; // Importe o arquivo de estilos


function Home() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  const searchMovies = async () => {
    const url = `http://localhost:8080/movies/search/${query}?language=pt-BR`;
    try {
      const response = await axios.get(url);
      setResults(response.data.results);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  return (
    <div className="search-container">
      <div className="results-container">
        <h1>Buscar Filme</h1>
        <input
          type="text"
          placeholder="Digite o nome do Filme"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button onClick={searchMovies}>Buscar</button>

        <div className="movie-cards-container">
          {results.map((movie) => (
            <div key={movie.id} className="movie-card">
              <h3>{movie.title}</h3>
              <img src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`} alt={movie.title} />
              <p dir="auto">{movie.overview}</p>
            </div>
          ))}
        </div>
      </div>
      {/* Adicione cards laterais ou outros elementos à direita se necessário */}
    </div>
  );
}

export default Home;
